css:
	.body {
			margin: 0;
			padding: 0;
			page-break-after: avoid;
			page-break-inside: avoid;
			page-break-before: avoid;
		}
    .container{
        width: 92%;
        margin-left: 3%;
        margin-right: 3%;
    }
    .header{
        display: flex;
    
    }
    .logo{
        width: 30%;
        
    }
    .logo img{
        height: 87px;
        width: 190px;
        margin-top: 5px;
        margin-left: 22px;

    }
    .company_name{
        
        width: 30%;
        font-size: 16px;
        font-weight: bold;
    }
   
    .address{
        width: 25%;
        margin-left: 30px;
    
    }
    .left{
        width: 24%;
    }
    .right{
        width: 27%;
        float: right;
        margin-left: 41px;
    }
    .center{
        width: 43%;
    }

    .size{
        display:flex;
    }
    @page {
    size: A4;
    margin:0;
  
    }
    p{
        font-weight: bold; 
    }

    @media print {
    .container {
        padding:0!important;
    }
    }
    .table{
        margin-top:5px!important;
        margin-bottom:5px!important;
        padding:0px!important;
    
    }
    .print-format td, .print-format th {
        padding: 3px !important;
    }
    tr td{
        margin: 0!important;
        padding: 0!important;
    }
    .table-active{
        background-color:#dfdfdf!important;
        color:white!important;
    }
